import React from "react";

interface Movie {
    imdbID: string;
    Year: string;
    Poster: string;
    Title: string;
    Type: string;
}

export const MoviePage: React.FC<{ movie: Movie }> = ({ movie }) => {
    const { imdbID, Year, Poster, Title, Type } = movie;
    return(
        <>
          <div>
            <p>{Year}</p>
          </div>

          <div>
            <img src={Poster !== "N/A" ? Poster : "https://via.placeholder.com/400"} alt={Title} />
          </div>

          <div>
            <span>{Type}</span>
            <h3>{Title}</h3>
          </div>
        </>
    )
}
