import { useState } from "react"
import { useGetMovie } from "../hook/useGetMovie";
import { MovieList } from "../components/Movie/MovieList";

export const API = () => {
    const [isEnabled, setIsEnabled] = useState(false); 
    const query = useGetMovie(isEnabled);
    const [visible, setVisible] = useState(true);

    const toggleButton = () => {
        setVisible(!visible)
    }
    return(
        <div className="m-5 grid justify-center">
            <h2 className="grid justify-center">API</h2>
            <p className="grid justify-center">data</p>
            <div>
                {visible &&
                <button className="border rounded-xl p-2 hover:bg-gray-300 hover:text-white" type="submit" 
                    onClick={() => {
                        setIsEnabled(true);
                        toggleButton();
                        }}>
                        Fetch Posts
                </button>}
            </div>
            <MovieList isEnabled={isEnabled} query={query}></MovieList>
        </div>
    )
}
