import { useQuery } from "@tanstack/react-query";
import { movieAPI } from "./../service/api/movies";

export const useGetMovie = (isEnabled: boolean = true) => {
    return useQuery({
        queryFn: movieAPI.getMovies,
        enabled: isEnabled
    })
}
